import { Component } from '@angular/core';

@Component({
  selector: 'app-about',
  templateUrl: './about.component.html',
  styleUrls: ['./about.component.scss']
})
export class AboutComponent {

}
<section id="about" class="py-20 bg-gray-900 text-white">
  <div class="container mx-auto text-center">
    <h2 class="text-4xl font-bold">About Me</h2>
    <p class="mt-4 text-lg max-w-2xl mx-auto">
      I'm a passionate Frontend Developer with expertise in Angular and modern
      web technologies. I love building interactive and optimized web
      applications.
    </p>
  </div>
</section>
